TEST(BinaryParser, getBinaryParserConfigErrors)
{
    auto fn = [](std::string in) -> json::Json
    {
        json::Json doc;
        doc.setString(in);
        return doc;
    };

    std::vector<TestCase> testCases {
        TestCase {"192.168.0.1", false, {}, Options {""}, fn("{}"), 0},
        TestCase {"192.168.0.1", false, {}, Options {"", ""}, fn("{}"), 0},
        TestCase {"192.168.0.1", false, {}, Options {"", "", ""}, fn("{}"), 0},
    };

    for (auto t : testCases)
    {
        runTest(t, hlp::getKVParser);
    }
}
